CREATE or replace VIEW sale_receipt_view AS SELECT s.uuid,r.organizationId,s.itemNumber,s.receiptNumber, r.organizationalUnitNr,s.articleNr,s.description,s.cashier,r.posNr,r.customerGroup,s.sector,s.cost,s.itemPrice,s.baseItemPrice,(s.baseItemPrice-s.netItemPrice) as itemDiscount,s.quantity,s.grossItemPrice,s.netItemPrice,s.cost as purchasePrice, FORMAT((s.cost-s.netItemPrice),2) as netLoss,s.bookingTime,s.commodityGroup FROM sale s JOIN receipt r ON (s.receipt = r.uuid);

SELECT s.uuid,r.organizationId,s.itemNumber,s.receiptNumber, r.organizationalUnitNr,s.articleNr,s.description,s.cashier,r.posNr,r.customerGroup,s.sector,s.cost,s.itemPrice,
s.baseItemPrice,(s.baseItemPrice-s.netItemPrice) as itemDiscount,s.quantity,s.grossItemPrice,s.netItemPrice,s.cost as purchasePrice, 
FORMAT((s.cost-s.netItemPrice),2) as netLoss,s.bookingTime,s.commodityGroup 
FROM sale s JOIN receipt r ON (s.receipt = r.uuid) 
inner JOIN articleCode ar ON (ar.code = s.itemNumber)
inner JOIN supplierItemPrice sip ON (sip.uuid = ar.uuid)
inner JOIN kSupplier ks ON (ks.uuid = sip.supplier)

CREATE or replace VIEW sale_receipt_view AS  SELECT s.uuid,r.organizationId,ks.uuid as supplierId,s.itemNumber,s.receiptNumber, r.organizationalUnitNr,s.articleNr,s.description,s.cashier,r.posNr,r.customerGroup,s.sector,s.cost,s.itemPrice,
s.baseItemPrice,(s.baseItemPrice-s.netItemPrice) as itemDiscount,s.quantity,s.grossItemPrice,s.netItemPrice,s.cost as purchasePrice, 
FORMAT((s.cost-s.netItemPrice),2) as netLoss,s.bookingTime,s.commodityGroup,cg.name as commodityGroupName,ks.name as supplierName
 from sale s, kSupplier ks,receipt r,commodityGroup cg where s.receipt = r.uuid and s.commodityGroup=cg.uuid and ks.uuid in( select supplier from  supplierItemPrice where uuid in (SELECT uuid FROM articleCode where code=s.itemNumber));

ALTER TABLE kSupplier ADD INDEX  kSupplier_uuid (uuid);
ALTER TABLE articleCode ADD INDEX  articleCode_code (code);
ALTER TABLE supplierItemPrice ADD INDEX  supplierItemPrice_uuid (uuid);
ALTER TABLE receipt ADD INDEX  receipt_uuid (uuid);
ALTER TABLE sale ADD INDEX  sale_receipt (receipt);
ALTER TABLE commodityGroup ADD INDEX  commodityGroup_uuid (uuid);


CREATE or replace VIEW sale_receipt_newview AS  SELECT s.uuid,r.organizationId,ks.uuid as supplierId,s.itemNumber,s.receiptNumber, r.organizationalUnitNr,s.articleNr,s.description,s.cashier,r.posNr,r.customerGroup,s.sector,s.cost,s.itemPrice,
s.baseItemPrice,(s.baseItemPrice-s.netItemPrice) as itemDiscount,s.quantity,s.grossItemPrice,s.netItemPrice,s.cost as purchasePrice, 
FORMAT((s.cost-s.netItemPrice),2) as netLoss,s.bookingTime,s.commodityGroup,cg.name as commodityGroupName,ks.name as supplierName
 from sale s, kSupplier ks,receipt r,commodityGroup cg
 where s.receipt = r.uuid and s.commodityGroup=cg.uuid 
 and ks.uuid NOT IN( select supplier from  supplierItemPrice 
 where uuid in (SELECT uuid FROM articleCode where code=s.itemNumber));


